import React, { useState } from 'react';
import NoteForm from './Components/NoteForm';
import NoteList from './Components/NoteList';
type Note = {
  title: string;
  content: string;
};

const App: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]); // State for all notes
  const [editingIndex, setEditingIndex] = useState<number | null>(null); // Track which note is being edited

  // Add a new note
  const addNote = (title: string, content: string) => {
    setNotes([...notes, { title, content }]);
  };

  // Delete a note
  const deleteNote = (index: number) => {
    setNotes(notes.filter((_, i) => i !== index)); // Remove the note with the specified index
    if (editingIndex === index) setEditingIndex(null); // Reset editing if the deleted note is being edited
  };

  // Update an existing note
  const updateNote = (title: string, content: string) => {
    if (editingIndex !== null) {
      const updatedNotes = [...notes];
      updatedNotes[editingIndex] = { title, content }; // Update the note at the editingIndex
      setNotes(updatedNotes);
      setEditingIndex(null); // Reset editing mode
    }
  };

  // Check if we are in editing mode and the editingIndex is valid
  const noteBeingEdited = editingIndex !== null ? notes[editingIndex] : null;

  return (
    <div>
      <h1>Note Taking App</h1>

      {/* Form for adding or editing notes */}
      <NoteForm
        onSave={editingIndex === null ? addNote : updateNote} // Either add a new note or update an existing one
        initialTitle={noteBeingEdited ? noteBeingEdited.title : ''} // Safely access the title
        initialContent={noteBeingEdited ? noteBeingEdited.content : ''} // Safely access the content
      />

      {/* List of Notes */}
      <NoteList
        notes={notes}
        onDeleteNote={deleteNote}
        onEditNote={(index) => setEditingIndex(index)} // Set the index of the note to be edited
      />
    </div>
  );
};

export default App;
