import React, { useState } from 'react';

type NoteFormProps = {
  onSave: (title: string, content: string) => void;
  initialTitle?: string;
  initialContent?: string;
};

const NoteForm: React.FC<NoteFormProps> = ({ onSave, initialTitle = '', initialContent = '' }) => {
  const [title, setTitle] = useState(initialTitle); // State to handle the note's title
  const [content, setContent] = useState(initialContent); // State to handle the note's content

  // Handle form submission
  const handleSubmit = () => {
    if (title.trim() && content.trim()) {
      onSave(title, content); // Call onSave to either add or update the note
      setTitle(''); // Clear form after saving
      setContent('');
    }
  };

  return (
    <div>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
      />
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="Content"
      />
      <button onClick={handleSubmit}>Save</button>
    </div>
  );
};

export default NoteForm;
